changeSet.sql:
DROP DATABASE IF EXISTS `default`;
CREATE DATABASE `default` CHARSET = utf8 COLLATE = utf8_general_ci;
USE `default`;

CREATE TABLE `user` (
`id` INT NOT NULL AUTO_INCREMENT,
`first_name` varchar(45) DEFAULT NULL,
`last_name` varchar(45) DEFAULT NULL,
`user_name` varchar(45) DEFAULT NULL,
PRIMARY KEY(`id`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=latin1;

CREATE TABLE `meeting`(
`id` INT AUTO_INCREMENT,
`info` varchar(100) not null,
`owner_id` INT,
`creation_date` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
PRIMARY KEY(`id`),
FOREIGN KEY (`owner_id`) REFERENCES `user`(`id`)
) ENGINE=INNODB;

CREATE TABLE `user_meeting`(
`user_id` INT NOT NULL,
`meeting_id` INT NOT NULL,
CONSTRAINT `Constr_CourseMembership_Student_fk`
FOREIGN KEY `user_fk` (`user_id`) REFERENCES `user` (`id`)
ON DELETE CASCADE ON UPDATE CASCADE,
CONSTRAINT `Constr_CourseMembership_Course_fk`
FOREIGN KEY `Course_fk` (`meeting_id`) REFERENCES `meeting` (`id`)
ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=INNODB;
